mapper.sql:
		FROM 
			codeGroup a
		WHERE 1=1
			AND a.cgUseNy = #{shUseNy}
       		AND a.cgDelNy = #{shDelNy} 				


					AND a.cgRegiDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}


					AND a.cgUpdtDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}



	            AND a.cgSeq = #{shValue}
	            AND a.cgName LIKE CONCAT('%',#{shValue},'%')
	            AND a.cgNameEng LIKE CONCAT('%',#{shValue},'%')

		ORDER BY a.cgSeq DESC
