mapper.sql:
		FROM 
			game a
		WHERE 1=1
       		AND a.gDelNy = #{shDelNy} 				


					AND a.gRegiDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}


					AND a.gUpdtDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}


			AND a.gCategoryCd = #{shCategoryCd}
			AND a.gOfficialCd = #{shOfficialCd}	
	        AND a.gUseAge >= #{shUseAge}			
			AND #{shPeople} >= a.gMinPeople AND a.gMaxPeople >= #{shPeople}			
	        AND #{shTime} >= a.gMinTime AND a.gMaxTime >= #{shTime}				
			AND a.gLevel >= #{shMinLevel}	
			AND #{shMaxLevel} >= a.gLevel		

	            AND a.gSeq = #{shValue}
	            AND a.gName LIKE CONCAT('%',#{shValue},'%')
	            AND a.gBestPeople = #{shValue}
	            AND a.ggRecommendPeople = #{shValue}

mapper.select:
SELECT *
FROM (
	SELECT


	ORDER BY a.gSeq DESC
) AS sub_games
WHERE 1=1
	AND sub_games.grScoreAvg >= #{shMinScoreAvg}	
	AND #{shMaxScoreAvg} >= sub_games.grScoreAvg
LIMIT #{rowNumToShow} OFFSET #{startRnumForMysql}

		SELECT COUNT(*)
		FROM 
			gameReview a
			LEFT JOIN game b ON a.game_gSeq = b.gSeq
			LEFT JOIN member c ON a.member_mSeq = c.mSeq
		WHERE 1=1
       		AND a.grDelNy = #{shDelNy} 				


					AND a.grRegiDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}



	            AND a.grSeq = #{shValue}
	            AND b.gSeq = #{shValue}
	            AND b.gName LIKE CONCAT('%',#{shValue},'%')
	            AND c.mSeq = #{shValue}
	            AND c.mId LIKE CONCAT('%',#{shValue},'%')
	            AND c.mName LIKE CONCAT('%',#{shValue},'%')

		ORDER BY a.grSeq DESC

SELECT a.grSeq, a.member_mSeq, a.game_gSeq, a.grScore, a.grComment, a.grRegiDate, b.gName, c.mId, c.mName FROM gameReview a LEFT JOIN game b ON a.game_gSeq = b.gSeq LEFT JOIN member c ON a.member_mSeq = c.mSeq WHERE 1=1 AND a.grDelNy = #{shDelNy}
AND a.grRegiDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}
AND a.grSeq = #{shValue} AND a.game_gSeq = #{shValue} AND b.gName LIKE CONCAT('%',#{shValue},'%') AND a.member_mSeq = #{shValue} AND c.mId LIKE CONCAT('%',#{shValue},'%') AND c.mName LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.gSeq DESC LIMIT #{rowNumToShow} OFFSET #{startRnumForMysql}
