mapper.sql:
		FROM 
			game a
		WHERE 1=1
       		AND a.gDelNy = #{shDelNy} 				


					AND a.gRegiDate BETWEEN #{shDateStart} AND #{shDateEnd}


					AND a.gUpdtDate BETWEEN #{shDateStart} AND #{shDateEnd}


			AND a.gLevel >= #{shMinLevel}	
			AND #{shMaxLevel} >= a.gLevel	
			AND a.rScoreAvg >= #{shMinScoreAvg}	
			AND #{shMaxScoreAvg} >= a.rScoreAvg	
			AND a.gMinPeople >= #{shMinPeople}	
			AND #{shMaxPeople} >= a.gMaxPeople			
	        AND a.gUseAge >= #{shUseAge}
	        AND a.gMinTime >= #{shMinTime}	
			AND #{shMaxTime} >= a.gMaxTime
			AND a.gOfficialCd = #{shOfficial} 	

	            AND a.gSeq = #{shValue}
	            AND a.gName LIKE CONCAT('%',#{shValue},'%')
	            AND a.gBestPeople = #{shValue}
	            AND a.ggRecommendPeople = #{shValue}

		ORDER BY a.gSeq DESC
