mapper.sql:
FROM memberSales a LEFT JOIN game b ON a.game_gSeq = b.gSeq LEFT JOIN member c ON a.member_mSeq = c.mSeq WHERE 1=1 AND a.msDelNy = 0 AND a.msStateCd = #{shStateCd}
AND a.msRegiDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}
AND b.gName LIKE CONCAT('%',#{shValue},'%') AND c.mName LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.msSeq DESC

FROM memberSales a LEFT JOIN game b ON a.game_gSeq = b.gSeq LEFT JOIN member c ON a.member_mSeq = c.mSeq LEFT JOIN memberSalesOrder d ON a.msSeq = d.memberSales_msSeq LEFT JOIN ( SELECT e.mName, e.mSeq FROM member e ) AS om ON d.oMember_mSeq = om.mSeq WHERE 1=1 AND a.msDelNy = 0 AND a.member_mSeq = #{member_mSeq} AND a.msStateCd = #{shStateCd} AND d.msoDeliStateCd = #{shDeliStateCd}
AND a.msRegiDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}
AND b.gName LIKE CONCAT('%',#{shValue},'%') AND om.mName LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.msSeq DESC

FROM memberSales a LEFT JOIN game b ON a.game_gSeq = b.gSeq LEFT JOIN member c ON a.member_mSeq = c.mSeq LEFT JOIN memberSalesOrder d ON a.msSeq = d.memberSales_msSeq WHERE 1=1 AND a.msDelNy = 0 AND d.oMember_mSeq = #{oMember_mSeq} AND d.msoDeliStateCd = #{shDeliStateCd}
AND d.msoRegiDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}
AND d.msoSeq = #{shValue} AND b.gName LIKE CONCAT('%',#{shValue},'%') AND c.mName LIKE CONCAT('%',#{shValue},'%')
ORDER BY d.msoSeq DESC

mapper.update:
UPDATE memberSales
SET msHit = msHit + 1
WHERE msSeq=#{msSeq};

mapper.select:
SELECT COUNT(*) FROM 
	memberSales 
WHERE 1=1
	AND member_mSeq=#{member_mSeq}
	AND game_gSeq=#{game_gSeq}

mapper.update:
UPDATE memberSalesOrder
SET 
	msoDeliStateCd = 53,
	msoDeliCompCd = #{msoDeliCompCd},
	msoDeliNo = #{msoDeliNo}
WHERE msoSeq = #{msoSeq}
